import{_ as a,c as t,a0 as i,o}from"./chunks/framework.KyugPFx6.js";const g=JSON.parse('{"title":"2024年秋季招新安排 👏🆕🧑‍🤝‍🧑","description":"","frontmatter":{},"headers":[],"relativePath":"new_members/join_us_2024.md","filePath":"new_members/join_us_2024.md"}'),r={name:"new_members/join_us_2024.md"};function e(s,l,n,u,h,c){return o(),t("div",null,l[0]||(l[0]=[i('<h1 id="_2024年秋季招新安排-👏🆕🧑‍🤝‍🧑" tabindex="-1">2024年秋季招新安排 👏🆕🧑‍🤝‍🧑 <a class="header-anchor" href="#_2024年秋季招新安排-👏🆕🧑‍🤝‍🧑" aria-label="Permalink to &quot;2024年秋季招新安排 👏🆕🧑‍🤝‍🧑&quot;">​</a></h1><h2 id="🎯-我们的目标" tabindex="-1">🎯 我们的目标 <a class="header-anchor" href="#🎯-我们的目标" aria-label="Permalink to &quot;🎯 我们的目标&quot;">​</a></h2><ul><li>找到对电子设计感兴趣的志同道合的人，以电子设计竞赛等为目标，共同在实验室度过一段有意义的时光！</li></ul><h2 id="✔-我们对成员的要求" tabindex="-1">✔ 我们对成员的要求 <a class="header-anchor" href="#✔-我们对成员的要求" aria-label="Permalink to &quot;✔ 我们对成员的要求&quot;">​</a></h2><ul><li>能管理好自己的时间，学习竞赛两不误</li><li>爱护实验仪器，不浪费实验室资源</li><li>坦率待人，维持好人际关系，友善对待新人</li><li>能适应实验室环境，为实验室运行出一份力</li></ul><hr><p>​</p><div class="warning custom-block"><p class="custom-block-title">如果你对电子设计感兴趣</p><p>欢迎加入Triz团队，此时你需要以下准备✨</p></div><h2 id="💡-材料准备" tabindex="-1">💡 材料准备 <a class="header-anchor" href="#💡-材料准备" aria-label="Permalink to &quot;💡 材料准备&quot;">​</a></h2><ul><li><strong>STM32F103C8T6 最小系统板</strong></li><li><strong>ST-Link 下载器</strong></li><li><strong>面包板</strong></li><li><strong>LED</strong></li><li><strong>按键</strong></li><li><strong>拨码旋钮</strong></li><li>......</li></ul><h2 id="💻-时间表与内容" tabindex="-1">💻 时间表与内容 <a class="header-anchor" href="#💻-时间表与内容" aria-label="Permalink to &quot;💻 时间表与内容&quot;">​</a></h2><div class="note custom-block github-alert"><p class="custom-block-title">NOTE</p><p></p><p>以下安排为大致安排，详细请以招新群通知为准</p></div><h3 id="_10月13日-参观实验室与学习" tabindex="-1">10月13日 - 参观实验室与学习 <a class="header-anchor" href="#_10月13日-参观实验室与学习" aria-label="Permalink to &quot;10月13日 - 参观实验室与学习&quot;">​</a></h3><ul><li><strong>活动内容</strong>: <ul><li>参观实验室，熟悉工作环境与设备。</li><li>学习 <strong>PCB 基础原理图</strong> 的绘制方法。</li></ul></li><li><strong>重点</strong>: 了解原理图设计基础，为后续制作 PCB 打下基础。</li></ul><hr><h3 id="_10月20日-pcb-layout-与下单" tabindex="-1">10月20日 - PCB Layout 与下单 <a class="header-anchor" href="#_10月20日-pcb-layout-与下单" aria-label="Permalink to &quot;10月20日 - PCB Layout 与下单&quot;">​</a></h3><ul><li><strong>活动内容</strong>: <ul><li>使用软件进行 <strong>PCB Layout</strong> 设计。</li><li>在嘉立创平台上提交订单，制作个人 PCB。</li></ul></li><li><strong>重点</strong>: 掌握 PCB 设计工具和下单流程。</li></ul><hr><h3 id="_10月27日-焊接练习与自制-pcb" tabindex="-1">10月27日 - 焊接练习与自制 PCB <a class="header-anchor" href="#_10月27日-焊接练习与自制-pcb" aria-label="Permalink to &quot;10月27日 - 焊接练习与自制 PCB&quot;">​</a></h3><ul><li><strong>活动内容</strong>: <ul><li><strong>焊接练习</strong>: 掌握元器件焊接技能。</li><li>自制 PCB 焊接实践。</li></ul></li><li><strong>重点</strong>: 提高动手能力，熟悉元器件焊接工艺。</li></ul><hr><h3 id="_11月17日-c-语言基础与-stm32-单片机使用" tabindex="-1">11月17日 - C 语言基础与 STM32 单片机使用 <a class="header-anchor" href="#_11月17日-c-语言基础与-stm32-单片机使用" aria-label="Permalink to &quot;11月17日 - C 语言基础与 STM32 单片机使用&quot;">​</a></h3><ul><li><strong>活动内容</strong>: <ul><li>学习 <strong>C 语言基本语法</strong>。</li><li>了解 STM32 单片机的基础知识，包括： <ul><li>环境配置</li><li>程序下载及调试</li></ul></li></ul></li><li><strong>重点</strong>: 掌握 C 语言语法，为后续 STM32 开发奠定基础。</li></ul><hr><h3 id="_11月24日-stm32-外设基本使用" tabindex="-1">11月24日 - STM32 外设基本使用 <a class="header-anchor" href="#_11月24日-stm32-外设基本使用" aria-label="Permalink to &quot;11月24日 - STM32 外设基本使用&quot;">​</a></h3><ul><li><strong>活动内容</strong>: <ul><li>学习 STM32 单片机的 <strong>外设使用方法</strong>，涉及外部硬件与 STM32 的连接与控制。</li></ul></li><li><strong>重点</strong>: 掌握 STM32 外设控制的基础技能，能够进行简单的外设调试和应用。</li></ul><hr><h3 id="_11月24日-寒假-综合考核安排" tabindex="-1">11月24日 - 寒假 综合考核安排 <a class="header-anchor" href="#_11月24日-寒假-综合考核安排" aria-label="Permalink to &quot;11月24日 - 寒假 综合考核安排&quot;">​</a></h3><ul><li><p>综合考核主要内容是培训内容的提升，由各参与成员根据题目<strong>自己制作</strong>相应成果并验收展示。</p><blockquote><p>具体内容详见通知群安排</p></blockquote></li><li><p>综合考核是衡量大家能否最终进入实验室的重要项目之一，需要大家<strong>认真对待</strong>📈。</p></li></ul><h2 id="📍-重要提示" tabindex="-1">📍 重要提示 <a class="header-anchor" href="#📍-重要提示" aria-label="Permalink to &quot;📍 重要提示&quot;">​</a></h2><ul><li>每次活动请携带<strong>笔记本电脑</strong>，确保 STM32 等开发环境已安装并能正常运行！</li><li>培训内容均为入门基础内容，希望大家都能在培训的过程中收获坚实的基础，<strong>主动</strong>学习求知探索！</li></ul><h2 id="✉-联系方式" tabindex="-1">✉ 联系方式 <a class="header-anchor" href="#✉-联系方式" aria-label="Permalink to &quot;✉ 联系方式&quot;">​</a></h2><div class="danger custom-block"><p class="custom-block-title">招新QQ群 ✨</p><p>神秘代码：732147811</p></div><blockquote><p>入群记得备注年级-专业-姓名-学号，否则可能不能通过，群内管理员均和蔼可亲，欢迎大家随时提问，积极参与实验室实践，提升动手能力！🎉</p></blockquote>',34)]))}const b=a(r,[["render",e]]);export{g as __pageData,b as default};
